import React from 'react';
import { Grid } from '@material-ui/core';
import { useSelector } from 'react-redux';
import SearchBar from "material-ui-search-bar";
import { searchFevcards,getFevcards } from '../../actions/fevcards';
import Fevcard from './Fevcard/Fevcard'
import useStyles from './styles';
import { useDispatch } from 'react-redux';

const Fevcards = ({ searchData,setCurrentId, }) => {
  console.log('setCurrentId',setCurrentId)
  const fevcards = useSelector((state) => state.fevcards);
  const searchcards = useSelector((state) => state.searchcards);

  const dispatch = useDispatch();
  const classes = useStyles();
  const updateSearchQuery = (search) => {
    fetch(
      `http://localhost:5000/festivals/${search}`,
      {
        method: "GET",
        headers: new Headers({
          Accept: "application/json"
        })
      }
    ).then((res)=>{
      console.log("Resss",res)
    })
    // console.log('abc', abc)
    // dispatch(getFevcards()).then((res)=>{
    //   console.log("res",res)
    // });
  }
  console.log('favCard',searchcards)

  return (
      <Grid className={classes.container} >
          <SearchBar name="search" fullWidth  onChange={(e) => updateSearchQuery(e) }
     style={{
       margin: '0 auto',
       maxWidth: 400
     }}
  />
  <br></br>
          <Grid  item xs={12} sm={6} md={6}>
            <Fevcard fevcard={fevcards} setCurrentId={setCurrentId} />
          </Grid>
      </Grid>
  );
};

export default Fevcards;
