import { FETCH_ALL, CREATE, SEARCH} from '../constants/actionTypes';

export default  (posts = [], action) => {
  switch (action.type) {
    case FETCH_ALL:
      return action.payload;
    case CREATE:
      return [...posts, action.payload];
      // case SEARCH:
      //   return [...posts, action.payload];
    default:
      return posts;
  }
};
